% Evaluation of a function
%
% Emanuele Ruffaldi 2017 @ SSSA
function [zm,Czz,Cxz, Zs] = manievalh(mx,mz,Xs,vXs,h_fx,wsigmax)
        
    if nargin < 6
        wsigmax = mx.wsigma;
    end
    if isempty(h_fx) == 0
        %[Xs,vXs] = manisigmas(mx,xp,Pp,wsigmax);   % [S,Gx] and [S,Ax]
        cXs = maniunpack(mx,Xs);     % [S,Cx]                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   
        cZs = cell(size(Xs,1),mz.count);  % [S,Cz]
        for I=1:size(cXs,1)
            [cZs{I,:}] = h_fx(cXs{I,:});
        end
        Zs = manipack(mz,cZs);
        [zm,Czz,Cxz] = maniunsigma(mz,Zs,wsigmax,vXs); % [Gz,Gx]
    else
        % identity function (assert mx == mz)
        [zm,Czz,Cxz] = maniunsigma(mz,Xs,wsigmax,vXs); % [Gz,Gx]
    end